const FormData = require('form-data');
const fetch = require('node-fetch');
const formData = new FormData();
const fs = require('fs');

const verifyEntities = [{
  name: "Jane Customer",
  address: "1000 Walnut Kansas City MO",
  company: "Smith & Sons",
  id_number: "92914567",
  strings: [{key: "phone", input: "929-7113-145"}]
}];

const tags = [{text: "onboarding"}, {text: "new_customer"}];

formData.append("verify_entities", JSON.stringify(verifyEntities));
formData.append("tags", JSON.stringify(tags));
formData.append("file", fs.createReadStream("DOCUMENT_FILE_PATH"));

const customer_id = "CUSTOMER_ID";

const url = `https://api.inscribe.ai/api/v2/customers/${customer_id}/documents`;
const options = {
  method: 'POST',
  headers: {
    Authorization: 'Inscribe ...'
  },
  body: formData,
};

fetch(url, options)
  .then(res => res.json())
  .then(json => console.log(json))
  .catch(err => console.error('error:' + err));

